import {express} from "express";
const api = express();
import { urlencoded, json } from "@adonisjs/bodyparser";
import { create } from './adonisLampex/app/Controllers/Http/UserController';

api.use(urlencoded({extended: false}));
api.use(json());

api.get('/contact-us-lize', function(req, res){
    res.render('/contact-us-lize')
});

api.post('/contact-us-lize', function(req, res){
    create({
        NomeDaEmpresa: req.body.inputNameEmpresa,
        CNPJ: req.body.inputCNPJ,
        Endereco: req.body.inputEndereco,
        Email: req.body.inputEmail,
        RespContrato: req.body.inputResContrato,
        TelRespContrato: req.body.inputTelContrato,
        RespProjeto: req.body.inputResProjeto,
        TelRespProjeto: req.body.inputTelProjeto
    }).then(function(){
        res.send("Empresa cadastrada com sucesso!")
    }).catch(function(error){
        res.send("Erro: Empresa n√£o cadastrada!" + error)
    });
});